import { INCREMENT } from "../actionTypes/action-types"

const InitialState = {
    count: 0,
}

const countReducer = (state = InitialState, {type, payload}) => {
    switch (type) {
        case INCREMENT:
            return {...state, count: state.count + payload}
            default:
                return state;
    }

}
export default countReducer;
